import {useState, useEffect} from 'react'
import './App.css';

function App() {
  const [timer, setTimer] = useState(0);
  const [timerOn, setTimerOn] = useState(false);

  useEffect(() => {
    let interval = null;

    if(timerOn) {
      interval = setInterval(() => {
        setTimer(prevTime => prevTime + 10)
      }, 10)
    } else {
      clearInterval(interval);
    }

    return () => {
      clearInterval(interval);
    }
  }, [timerOn])

  return (
    <div>
      <span>{("0" + Math.floor(timer / 3600000) % 24).slice(-2)}:</span>
      <span>{("0" + Math.floor(timer / 60000) % 60).slice(-2)}:</span>
      <span>{("0" + Math.floor(timer / 1000) % 60).slice(-2)}</span>
      <div className="App">

        <button type="button" onClick={() => setTimerOn(true)}>Start</button>
        <button type="button" onClick={() => setTimerOn(true)}>Stop</button>
        <button type="button" onClick={() => setTimer(0)}>Reset</button>
      </div>
    </div>
  );
}

export default App;
